package oops;
//4. Write a method to multiply 3 values and return the result.
public class Mutliply {
    public static int ops(int a,int b,int c) {
    	return a*b*c;
    }
	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int total=ops(10,20,30);
       System.out.println(total);
	}

}
